import React, { Component } from 'react';

class Table extends Component {
    constructor(props) {
        super(props);

        this.state = {
            mahasiswas: [
            ],
            newID: "",
            newMahasiswa: ""
        }

        this.setId = this.setId.bind(this);
        this.setMahasiswa = this.setMahasiswa.bind(this);
        this.tambahData = this.tambahData.bind(this);
        this.hapusData = this.hapusData.bind(this);
        this.renderTableData = this.renderTableData.bind(this);
    }

    setId(e) {
        this.setState({ newID: parseInt(e.target.value) }); // Mengonversi string menjadi angka
    }

    setMahasiswa(e) {
        this.setState({ newMahasiswa: e.target.value });
    }

    tambahData(e) {
        const { newID, newMahasiswa } = this.state;
        if (newID.trim() === '' || newMahasiswa.trim() === '') {
            alert('Kolom NIM dan NAMA harus diisi!!!');
            return; 
        }
    
        const newMahasiswaData = { id: newID, keluarga: newMahasiswa };
        this.setState(prevState => ({
            mahasiswas: [...prevState.mahasiswas, newMahasiswaData],
            newID: "",
            newMahasiswa: ""
        }));
    }
    

    hapusData(id) {
        this.setState(prevState => ({
            mahasiswas: prevState.mahasiswas.filter(data => data.id !== id)
        }));
    }

    renderTableData() {
        return this.state.mahasiswas.map((mahasiswaData, index) => {
            const { id, keluarga } = mahasiswaDataData;
            return (
                <tr key={id}>
                    <td>{id}</td>
                    <td>{keluarga}</td>
                    <td><button onClick={() => this.hapusData(id)}>Hapus</button></td>
                </tr>
            );
        });
    }

    render() {
        return (
            <div className="body">
                <h1>{this.props.judul}</h1>

                <table>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>NAMA</th>
                            <th>Aksi</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.renderTableData()}
                    </tbody>
                </table>

                <div style={styles.container}>
                    NIM: <input type="text" name="id" value={this.state.newID} onChange={this.setId} placeholder='Masukkan angka' required/>
                    &nbsp;&nbsp; 
                    NAMA: <input type="text" name="keluarga" value={this.state.newMahasiswa} onChange={this.setMahasiswa} required/>
                    &nbsp;
                    <button onClick={this.tambahData}>Tambah</button>
                </div>

                <div style={styles.container}>
                    Baru saja ditambahkan:
                    <br />ID: <span>{this.state.mahasiswas[this.state.mahasiswas.length - 1]?.id}</span>
                    <br />NAMA: <span>{this.state.mahasiswas[this.state.mahasiswas.length - 1]?.keluarga}</span>
                </div>
            </div>
        )
    }
}

const styles = {
    container: {
        border: '1px solid black',
        padding: '8px',
        marginTop: '8px',
    }
}

export default Table;
